import React from 'react';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      Nome: '',

    }
    this.handleChange = this.handleChange.bind(this); 
  }
  handleChange(event) {
    console.log('chamo')
    const name = event.target.name
    const value = name === 'Nome' ? event.target.value.toUpperCase() : event.target.value;
    this.setState({[name]: value});
  }
//   maxLength(numero) {
//     console.log('xablau');
//   }
  render() {
    return (
      <form>
        <fieldset>
          <input name="Nome" type="text" onChange={(event) => this.handleChange(event)} value={this.state.Nome} maxLength="40" />
        </fieldset>
      </form>

    );
  }
}

export default Form;
