import React from 'react';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      Nome: '',

    }
    this.handleChange = this.handleChange.bind(this); 
  }
  handleChange({ target }, maxLength) {
    const name = target.name
    const value = name === 'Nome' ? target.value.toUpperCase() : target.value;

    if (maxLength) {
      value = target.value.substring(0,maxLength);
    }



    this.setState({[name]: value});
  }
//   maxLength(numero) {
//     console.log('xablau');
//   }
  render() {
    return (
      <form>
        <fieldset value="">
          <input name="Nome" type="text" onChange={(event) => this.handleChange(event)} value={this.state.Nome} maxLength="40" />
        </fieldset>
      </form>

    );
  }
}

export default Form;
