import React from 'react';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      Nome: '',

    }
  }
  handleChange(event) {
    const value = event.target.name
    this.setState({[value]: event.target.value});
  }
  render() {
    return (
      <form>
        <fieldset>
          <input name="Nome" type="text" onChange={this.handleChange} />
        </fieldset>
      </form>

    );
  }
}

export default Form;
